import{_ as g,c as e,e as B,a1 as c}from"./chunks/framework.C-01xEl2.js";const i="data:image/png;base64,",l=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"menus/document/css/知识点/盒模型.md","filePath":"menus/document/css/知识点/盒模型.md","lastUpdated":1707281529000}'),o={name:"menus/document/css/知识点/盒模型.md"};function a(D,A,d,n,t,I){return B(),e("div",null,A[0]||(A[0]=[c('<h2 id="什么是盒模型" tabindex="-1">什么是盒模型 <a class="header-anchor" href="#什么是盒模型" aria-label="Permalink to &quot;什么是盒模型&quot;">​</a></h2><p>CSS 中组成一个块级盒子需要：</p><ul><li>Content box: 这个区域是用来显示内容，大小可以通过设置 width 和 height.</li><li>Padding box: 包围在内容区域外部的空白区域；大小通过 padding 相关属性设置。</li><li>Border box: 边框盒包裹内容和内边距。大小通过 border 相关属性设置。</li><li>Margin box: 这是最外面的区域，是盒子和其他元素之间的空白区域。大小通过 margin 相关属性设置。</li></ul><p>如下图</p><p><img src="'+i+'" alt="boxModel" loading="lazy"></p><h3 id="通常我们设置的宽高是设置哪些值" tabindex="-1">通常我们设置的宽高是设置哪些值 <a class="header-anchor" href="#通常我们设置的宽高是设置哪些值" aria-label="Permalink to &quot;通常我们设置的宽高是设置哪些值&quot;">​</a></h3><ul><li>在标准模型中，如果你给盒设置 width 和 height，实际设置的是 content box。 等价于 <code>box-sizing: content-box</code></li><li>在IE盒模型中，实际设置的是 content box + padding box + border box. 等价于 <code>box-sizing: border-box</code></li></ul><h2 id="参考" tabindex="-1">参考 <a class="header-anchor" href="#参考" aria-label="Permalink to &quot;参考&quot;">​</a></h2><ul><li><a href="https://developer.mozilla.org/zh-CN/docs/Learn/CSS/Building_blocks/The_box_model" target="_blank" rel="noreferrer">mdn盒模型</a></li></ul>',9)]))}const r=g(o,[["render",a]]);export{l as __pageData,r as default};
