import{_ as e,c as t,e as o,a1 as l}from"./chunks/framework.C-01xEl2.js";const b=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"menus/document/js/面试题汇总/localStorage和sessionStorage的异同.md","filePath":"menus/document/js/面试题汇总/localStorage和sessionStorage的异同.md","lastUpdated":1707281529000}'),r={name:"menus/document/js/面试题汇总/localStorage和sessionStorage的异同.md"};function s(i,a,n,d,c,h){return o(),t("div",null,a[0]||(a[0]=[l('<h2 id="localstorage和sessionstorage的异同" tabindex="-1">localStorage和sessionStorage的异同 <a class="header-anchor" href="#localstorage和sessionstorage的异同" aria-label="Permalink to &quot;localStorage和sessionStorage的异同&quot;">​</a></h2><h2 id="共同点" tabindex="-1">共同点 <a class="header-anchor" href="#共同点" aria-label="Permalink to &quot;共同点&quot;">​</a></h2><ul><li>都有共同原型Storage,意味着相同的操作api</li><li>存储大小有限，和浏览器相关，一般是5MB</li></ul><h2 id="差异" tabindex="-1">差异 <a class="header-anchor" href="#差异" aria-label="Permalink to &quot;差异&quot;">​</a></h2><table tabindex="0"><thead><tr><th></th><th>localStorage</th><th>sessionStorage</th></tr></thead><tbody><tr><td>作用范围</td><td>同一个浏览器同一个源</td><td>同一个浏览器同一个源同一个会话</td></tr><tr><td>自动清除</td><td>不会自动清除</td><td>浏览器会话关闭就自动清除</td></tr></tbody></table><h2 id="浏览器会话" tabindex="-1">浏览器会话 <a class="header-anchor" href="#浏览器会话" aria-label="Permalink to &quot;浏览器会话&quot;">​</a></h2><p>可以理解就是一个浏览器标签页，标签页不关闭，会话不停止</p><p>区别是标签页是浏览器中用于显示网页内容的单个选项卡，而会话是用户与网站之间的一段时间内的交互过程</p><h2 id="练习题" tabindex="-1">练习题 <a class="header-anchor" href="#练习题" aria-label="Permalink to &quot;练习题&quot;">​</a></h2><ul><li>谷歌浏览器打开两个窗口访问同个源，那localStorage还共享吗？ <blockquote><p>共享，localStorage区分只是不同浏览器不同源</p></blockquote></li><li>谷歌浏览器打开两个标签访问同个源，那sessionStorage还共享吗？ <blockquote><p>不共享，一个标签页有自己独自的会话</p></blockquote></li><li>浏览器在一个标签页访问a网站，然后切换成b网站再切回a网站，这时候a网站sessionStorage会清除吗 <blockquote><p>不会清除，标签不关闭，会话不停止</p></blockquote></li><li>用户登出了sessionStorage还存在吗 <blockquote><p>存在，sessionStorage的会话和前后端的会话不是一个概念，如果项目登出时sessionStorage不存在了看看登出时是否手动清除了</p></blockquote></li></ul><h2 id="关联面试题" tabindex="-1">关联面试题 <a class="header-anchor" href="#关联面试题" aria-label="Permalink to &quot;关联面试题&quot;">​</a></h2><ul><li><a href="/menus/document/js/面试题汇总/原型链.html">原型链</a></li><li><a href="/menus/document/brower/面试题汇总/在浏览器标签页输入一个url会发生什么.html">在浏览器标签页输入一个url会发生什么</a></li></ul><h2 id="参考" tabindex="-1">参考 <a class="header-anchor" href="#参考" aria-label="Permalink to &quot;参考&quot;">​</a></h2>',13)]))}const m=e(r,[["render",s]]);export{b as __pageData,m as default};
